note = ""
count = 0
for i in range(len(note)):
    if ('a' <= note[i] <= 'c'):
        lowercase = note[i]
        j = 1
        while (j < 1001):
            if (note[(i + j) % len(note)] == str.upper(lowercase)):
                count += 1
            if (i - j >= 0) and (note[(i - j) % len(note)] == str.upper(lowercase)):
                count += 1
            j += 1
